sys_script_client.script:
function onLoad() {
	try {
		g_form.showFieldMsg(
			'u_prompt_text_attribute', 
			'This is the attribute in the prompt JSON that contains the prompt that will be populated with record information for processing.',
			'info'
		);

		g_form.showFieldMsg(
			'u_prompt',
			'This is the JSON structure of the API request prompt that will be sent to the LLM. Check the documentation of the LLM API you are using to get find the basic prompt structure.',
			'info'
		);
	} catch(e) {
		g_form.addErrorMessage(`[Form Control - Help Messages] Error ${ e.message }`);
	}
}
