sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	try {
		var chatgptEndpoint = 'https://api.openai.com/v1/chat/completions';
		var grokEndpoint = 'https://api.x.ai/v1/chat/completions';
		var anthropicEndpoint = 'https://api.anthropic.com/v1/messages';
		var model = g_form.getValue('u_model');

		switch(model) {
			case 'chatgpt':
				g_form.setValue('u_api_endpoint', chatgptEndpoint);
				break;
			case 'grok':
				g_form.setValue('u_api_endpoint', grokEndpoint);
				break;
			case 'anthropic':
				g_form.setValue('u_api_endpoint', anthropicEndpoint);
				break;
			default:
				g_form.setValue('u_api_endpoint', '');
				break;
		}
	} catch(e) {
		g_form.addErrorMessage(`[Form Control - Default API Endpoint] Error: ${ e.message }`);
		throw new Error(`[Form Control - Default API Endpoint] Error: ${ e.message }`);
	}
}
