sys_script_client.script:
function onLoad() {
	try { 
		var docReference = g_form.getValue('u_document_reference');
		if(docReference) {
			g_form.getReference('u_document_reference', function(response) {
				var nullArr = [];

				if(!response.u_documentation) nullArr.push('Code Documentation');
				if(!response.u_script) nullArr.push('Script Annotation');

				if(nullArr.length > 0) g_form.showFieldMsg('u_document_reference', 'Document Reference is missing ' + nullArr.join(' and ') + '. Consider generating or writing documenation for this code!', 'warning');
			});
		} else {
			g_form.showFieldMsg('u_document_reference', 'Document reference is missing! This was likely caused by an application error. Please contact your administrator.', 'error');
		}
	} catch(e) {
		g_form.addErrorMessage('[Form Control - DocRef Suggestion] Error: ' + e.message);
	}
}
