sys_script_include.script:
/**
 * DocuGenLLMMarkdownUtils
 * AJAX handler to fetch Markdown content from u_docugen_suggestions or u_docugen_code_docs
 */

var DocuGenLLMMarkdownUtils = Class.create();
DocuGenLLMMarkdownUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getMarkdownContent: function() {
        try {
            const dataObj = JSON.parse(this.getParameter('sysparm_data_obj'));

            if (!dataObj.sys_id) {
                return JSON.stringify({ error: 'No sys_id provided' });
            }

            if (dataObj.table != 'u_docugen_suggestions' && dataObj.table != 'u_docugen_code_docs') {
                return JSON.stringify({ error: 'Invalid table specified' });
            }

            const mdGr = new GlideRecord(dataObj.table);
            mdGr.addQuery('sys_id', dataObj.sys_id);
            mdGr.query();

            if (mdGr.next()) {
                const markdown = mdGr[dataObj.return_field].toString() || `No content available. Consider generating ${ dataObj.field_suggestion } for this record!`;

                return JSON.stringify({ markdown: markdown });
            } else {
                return JSON.stringify({ error: 'Record not found' });
            }
        } catch (e) {
            gs.error(`[DocuGenLLMMarkdownUtils.getMarkdownContent] Error: ${e}`);
            return JSON.stringify({ error: 'Error fetching content: ' + e.message });
        }
    },

    type: 'DocuGenLLMMarkdownUtils'
});
