sys_script_include.script:
/**
 * DocuGenLLMNotificationUtils
 * Handles notifications for coverage report completion
 */

var DocuGenLLMNotificationUtils = Class.create();
DocuGenLLMNotificationUtils.prototype = {
    initialize: function() {},

    sendCoverageCompleteNotification: function(appSysId, userId) {
        try {
            var appGr = new GlideRecord('sys_app');
            if (appGr.get(appSysId)) {
                var notification = new GlideRecord('sysevent_email_action');
                notification.addQuery('name', 'DocuGenLLM Coverage Report Complete');
                notification.query();
                if (!notification.next()) {
                    notification.initialize();
                    notification.setValue('name', 'DocuGenLLM Coverage Report Complete');
                    notification.setValue('table', 'sys_app');
                    notification.setValue('template', 'docugen_coverage_complete');
                    notification.setValue('active', true);
                    notification.insert();
                }

                var event = new GlideRecord('sysevent');
                event.initialize();
                event.setValue('name', 'docugen.coverage.complete');
                event.setValue('parms', {
                    app_name: appGr.name.toString(),
                    app_sys_id: appSysId,
                    user_id: userId,
                    link: 'u_docugen_coverage_reports_list.do?sysparm_query=u_app_scope=' + appSysId
                });
                event.insert();
            }
        } catch (e) {
            gs.error(`[DocuGenLLMNotificationUtils.sendCoverageCompleteNotification] Error: ${e}`);
        }
    },

    type: 'DocuGenLLMNotificationUtils'
};
