sys_ui_page.client_script:
document.addEventListener('DOMContentLoaded', function() {
            // Get sys_id from URL parameter
            var urlParams = new URLSearchParams(window.location.search);
            var sysId = urlParams.get('sysparm_sys_id');
            var table = urlParams.get('sysparm_table');
			console.log(sysId, table);
            if (!sysId) {
                document.getElementById('markdownOutput').innerHTML = '<p class="error">Error: No sys_id provided</p>';
                return;
            }

            // Fetch Markdown content via AJAX
            var ga = new GlideAjax('global.DocuGenLLMMarkdownUtils');
            ga.addParam('sysparm_name', 'getMarkdownContent');
            ga.addParam('sysparm_sys_id', sysId);
            ga.addParam('sysparm_table', table);
            ga.getXML(function(response) {
                var answer = response.responseXML.documentElement.getAttribute('answer');
				console.log(answer);
                try {
                    var result = JSON.parse(answer);
                    if (result.error) {
                        document.getElementById('markdownOutput').innerHTML = '<p class="error">' + result.error + '</p>';
                        return;
                    }

                    // Parse Markdown to HTML using marked.js
                    var markdown = result.markdown || 'No content available';
                    var html = marked.parse(markdown);

                    // Sanitize HTML to prevent XSS
                    var sanitizedHtml = DOMPurify.sanitize(html);

                    // Render to the page
                    document.getElementById('markdownOutput').innerHTML = sanitizedHtml;
                } catch (e) {
                    document.getElementById('markdownOutput').innerHTML = '<p class="error">Error rendering content: ' + e.message + '</p>';
                }
            });
        });
