sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        var sysId = request.pathParams.sys_id;
        var table = request.pathParams.table;

        if (!sysId || !['u_docugen_code_docs', 'u_docugen_suggestions'].includes(table)) {
            response.setStatus(400);
            return { error: 'Invalid sys_id or table' };
        }

        var gr = new GlideRecord(table);
        if (!gr.get(sysId)) {
            response.setStatus(404);
            return { error: 'Record not found' };
        }

        if (!gr.canRead()) {
            response.setStatus(403);
            return { error: 'No read access to record' };
        }

        var content = {};
        if (table === 'u_docugen_code_docs') {
            content = {
                documentation: gr.u_documentation_markdown.toString(),
                annotations: gr.u_script.toString()
            };
        } else {
            content = {
                suggestions: gr.u_suggestions_markdown.toString(),
                code_review: gr.u_code_review.toString()
            };
        }

        response.setStatus(200);
        return {
            status: 'success',
            sys_id: sysId,
            table: table,
            content: content
        };
    } catch (e) {
        gs.error(`[DocuGenLLMAPI.getContent] Error: ${e}`);
        response.setStatus(500);
        return { error: 'Internal server error: ' + e.message };
    }
})(request, response);
