sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        var body = request.body.data;
        if (!body.sys_id || !body.table || !body.display_value) {
            response.setStatus(400);
            return { error: 'Missing required parameters: sys_id, table, display_value' };
        }

        var docGen = new DocuGenLLMClientUtils();
        var result = docGen.generateMaintenanceSuggestions(body.sys_id, body.table, body.display_value);

        if (result.status === 200) {
            response.setStatus(200);
            return {
                status: 'success',
                link: result.message.link,
                title: result.message.title,
                sys_id: result.message.sys_id
            };
        } else {
            response.setStatus(400);
            return { error: result.message };
        }
    } catch (e) {
        gs.error(`[DocuGenLLMAPI.generateMaintenanceSuggestions] Error: ${e}`);
        response.setStatus(500);
        return { error: 'Internal server error: ' + e.message };
    }
})(request, response);
